import math

def dot(a, b):
    return sum(x*y for x, y in zip(a, b))

def norm(a):
    return math.sqrt(sum(x*x for x in a)) or 1.0

def main(vec1: list, vec2: list):
    sim = dot(vec1, vec2) / (norm(vec1) * norm(vec2))
    return {"similarity": float(sim)}

